graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Complete Styling Demo</b></font></td></tr><tr><td align="center"><font point-size="10">v @style</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  rankdir="LR";
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", color="blue", shape="box"];
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i> <i>@critical</i></td></tr><tr><td align="left">Validate</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", fillcolor="#ffcccc", penwidth="3", color="#cc0000"];

  // Edges
  "process" -> "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>@style(color: green; penwidth: 2;)</b></td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", color="green", penwidth="2"];
}
