graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Custom Styling Example</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Initialize system</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "validatePayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validatePayment</b> <i>&lt;task&gt;</i> <i>@Critical</i></td></tr><tr><td align="left">Validate Payment</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3, fillcolor="#ffcccc", penwidth="3", color="#cc0000"];
  "checkBalance" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkBalance</b> <i>&lt;task&gt;</i> <i>@Warning</i></td></tr><tr><td align="left">Check Balance</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", fillcolor="#ffffcc", penwidth="2", color="#cccc00"];
  "processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Processing payment</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "completePayment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>completePayment</b> <i>&lt;task&gt;</i> <i>@Success</i></td></tr><tr><td align="left">Complete Payment</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", fillcolor="#ccffcc", penwidth="2", color="#00cc00"];
  "done" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>done</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Transaction complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "start" -> "validatePayment" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validatePayment" -> "checkBalance" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkBalance" -> "processing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processing" -> "completePayment" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "completePayment" -> "done" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
