graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Explicit Grid Assignment</b></font></td></tr><tr><td align="center"><font point-size="10">v @style</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "a" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>a</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", grid="true"];
  "b" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>b</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", grid="true"];
  "c" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>c</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", grid="true"];
  "d" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>d</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", grid="true"];

  // Grid layout
  "root__grid_rail_1" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];
  "root__grid_rail_2" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];
  "root__grid_rail_3" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];
  "root__grid_rail_1" -> "root__grid_rail_2" [style=invis];
  "root__grid_rail_2" -> "root__grid_rail_3" [style=invis];
  // Grid position 1
  { rank=same; "root__grid_rail_1"; "a"; "d"; }
  // Grid position 2
  { rank=same; "root__grid_rail_2"; "b"; }
  // Grid position 3
  { rank=same; "root__grid_rail_3"; "c"; }

}
