graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Grid Layout Example</b></font></td></tr><tr><td align="center"><font point-size="10">v @style</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  rankdir="LR";
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_DataProcessing {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>DataProcessing</b> <i>&lt;namespace&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    grid="3";
    "DataProcessing__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "fetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>fetchData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Fetch Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "validateData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validateData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "transformData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>transformData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Transform Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "filterData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>filterData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Filter Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "aggregateData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>aggregateData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Aggregate Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "storeData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>storeData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Store Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

    // Grid layout for cluster DataProcessing
    "cluster_DataProcessing__grid_rail_1" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];
    "cluster_DataProcessing__grid_rail_2" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];
    "cluster_DataProcessing__grid_rail_3" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];
    "cluster_DataProcessing__grid_rail_1" -> "cluster_DataProcessing__grid_rail_2" [style=invis];
    "cluster_DataProcessing__grid_rail_2" -> "cluster_DataProcessing__grid_rail_3" [style=invis];
    // Grid position 1
    { rank=same; "cluster_DataProcessing__grid_rail_1"; "fetchData"; "filterData"; }
    // Grid position 2
    { rank=same; "cluster_DataProcessing__grid_rail_2"; "validateData"; "aggregateData"; }
    // Grid position 3
    { rank=same; "cluster_DataProcessing__grid_rail_3"; "transformData"; "storeData"; }
  }

}
