graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Example</b></font></td></tr><tr><td align="center"><font point-size="10">v @style</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  rankdir="LR";
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "important" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>important</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Important task</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", fillcolor="yellow", penwidth="3"];
  "a" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>a</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "b" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>b</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "a" -> "b" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">@style(color: red; penwidth: 5;)</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", color="red", penwidth="5"];
}
