graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "apiEndpoint" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiEndpoint</b> <i>&lt;task&gt;</i> <i>@HTTP("GET") @Route("/api/users") @Auth("Bearer")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "handler" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>handler</b> <i>&lt;task&gt;</i> <i>@EventDriven @Topic("user.created")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "processor" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processor</b> <i>&lt;task&gt;</i> <i>@Batch @Schedule("0 0 * * *")</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

}
