graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>E-Commerce Order Processing</b></font></td></tr><tr><td align="center"><font point-size="10">@Version(&quot;1.0&quot;)</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "OrderConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>OrderConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout : Duration</td><td port="timeout__value" align="left" balign="left">PT5M</td></tr><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : number</td><td port="maxRetries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  subgraph cluster_OrderWorkflow {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>OrderWorkflow</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "OrderWorkflow__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "Received" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Received</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order received</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
    "Processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Processing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Processing order</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
    "Completed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Completed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order completed</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  }

  // Root-level notes
  "note_Payment_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Payment processing subsystem handles all<br/>financial transactions</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Payment_0" -> "Payment" [style=dashed, color="#999999", arrowhead=none];
  "note_Inventory_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Inventory management ensures accurate<br/>stock tracking</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Inventory_0" -> "Inventory" [style=dashed, color="#999999", arrowhead=none];
  "note_Shipping_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Shipping workflow coordinates with<br/>external carriers</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Shipping_0" -> "Shipping" [style=dashed, color="#999999", arrowhead=none];

  // Edges
  "Received" -> "Validate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Validate" -> "Process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Process" -> "Confirm" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Confirm" -> "Check" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Check" -> "Reserve" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Reserve" -> "Allocate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Allocate" -> "Calculate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Calculate" -> "Label" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Label" -> "Dispatch" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Dispatch" -> "Completed" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Received" -> "Processing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Processing" -> "Completed" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
