graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Condition Evaluation Context</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="maxRetries__key" align="left" balign="left">maxRetries</td><td port="maxRetries__value" align="left" balign="left">3</td></tr><tr><td port="errorCount__key" align="left" balign="left">errorCount</td><td port="errorCount__value" align="left" balign="left">0</td></tr><tr><td port="retryCount__key" align="left" balign="left">retryCount</td><td port="retryCount__value" align="left" balign="left">0</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Start</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Retry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Retry</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "End" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>End</b> <i>&lt;task&gt;</i></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "Start" -> "Process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Process" -> "Retry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when=errorCount &gt; 0</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", style=dashed, color="#9E9E9E", penwidth=1];
  "Process" -> "Retry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when=retryCount &lt; maxRetries</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD", style=solid, color="#4CAF50", penwidth=2];
  "Retry" -> "End" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
