graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Idle</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Running" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Running</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Paused" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Paused</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Stopped" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Stopped</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "Idle" -> "Running" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">start</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Running" -> "Paused" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">pause</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Paused" -> "Running" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">resume</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Running" -> "Stopped" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">stop</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
}
