graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Idle</b> <i>&lt;state&gt;</i></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L1:0-1:11"];
  "Running" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Running</b> <i>&lt;state&gt;</i></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L2:0-2:14"];
  "Paused" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Paused</b> <i>&lt;state&gt;</i></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L3:0-3:13"];
  "Stopped" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Stopped</b> <i>&lt;state&gt;</i></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L4:0-4:14"];

  // Edges
  "Idle" -> "Running" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>start</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L6:5-6:21"];
  "Running" -> "Paused" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>pause</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L7:8-7:23"];
  "Paused" -> "Running" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>resume</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L8:7-8:24"];
  "Running" -> "Stopped" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>stop</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L9:8-9:23"];
}
