graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "input" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>input</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Receive Input</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L1:0-1:27"];
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate Data</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L2:0-2:30"];
  "transform" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>transform</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Transform Data</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L3:0-3:32"];
  "output" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>output</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Send Output</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L4:0-4:26"];

  // Edges
  "input" -> "validate" [labelOverlay="75%", edgeURL="#L6:6-6:17"];
  "validate" -> "transform" [labelOverlay="75%", edgeURL="#L6:18-6:30"];
  "transform" -> "output" [labelOverlay="75%", edgeURL="#L6:31-6:40"];
}
