graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>LLM Processing Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "llmConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>llmConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">claude-3-5-sonnet-20241022</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature : Float</td><td port="temperature__value" align="left" balign="left">0.7</td></tr><tr><td port="maxTokens__key" align="left" balign="left">maxTokens : Integer</td><td port="maxTokens__value" align="left" balign="left">4096</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "userInput" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userInput</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="question__key" align="left" balign="left">question</td><td port="question__value" align="left" balign="left">#userQuestion</td></tr><tr><td port="context__key" align="left" balign="left">context</td><td port="context__value" align="left" balign="left">#conversationHistory</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze this question: {{<br/>userInput.question }}
Context: {{<br/>userInput.context }}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">{{ llmConfig.model }}</td></tr><tr><td port="temperature__key" align="left" balign="left">temperature</td><td port="temperature__value" align="left" balign="left">{{ llmConfig.temperature }}</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "enhance" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>enhance</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Enhance the analysis: {{ analyze.prompt<br/>}}</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="model__key" align="left" balign="left">model</td><td port="model__value" align="left" balign="left">{{ llmConfig.model }}</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "format" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>format</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Format the final response based on: {{<br/>enhance.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "analyze" -> "enhance" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "enhance" -> "format" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "analyze" -> "llmConfig" [label="reads model", style=dashed, color=blue];
  "analyze" -> "llmConfig" [label="reads temperature", style=dashed, color=blue];
  "analyze" -> "userInput" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "userInput" [label="reads prompt", style=dashed, color=blue];
  "enhance" -> "llmConfig" [label="reads model", style=dashed, color=blue];
  "enhance" -> "analyze" [label="reads prompt", style=dashed, color=blue];
  "format" -> "enhance" [label="reads prompt", style=dashed, color=blue];
}
