graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b><b>API Gateway</b> Documentation</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Config</b> <i>&lt;state&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="apiKey__key" align="left" balign="left">apiKey</td><td port="apiKey__value" align="left" balign="left"><font face="monospace" color="#6C757D">$ENV_API_KEY</font></td></tr><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">[API<br align="left"/>Docs](<u>https://api.example.com</u>)</td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Authenticate</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left"><b>Authenticate</b> using <i>OAuth 2.0</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="method__key" align="left" balign="left">method</td><td port="method__value" align="left" balign="left"><font face="monospace" color="#6C757D">POST /auth/token</font></td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "FetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>FetchData</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left"><i>Fetch</i> data from <b>remote</b> API</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left"><s>5s</s> <b>30s</b></td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Error</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Handle <font face="monospace" color="#6C757D">4xx</font> and <font face="monospace" color="#6C757D">5xx</font> errors</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Root-level notes
  "note_Authenticate_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Use <font face="monospace" color="#6C757D">Bearer</font> token in <b>Authorization</b><br/>header</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_Authenticate_0" -> "Authenticate" [style=dashed, color="#999999", arrowhead=none];

  // Edges
  "Config" -> "Authenticate" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "Authenticate" -> "FetchData" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "FetchData" -> "Error" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
