graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Markdown in Notes</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "Process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];


  // Notes
  "note_0_Process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Important</b>: Use <font face="monospace" color="#6C757D">--force</font> flag <i>only</i><br/>in [test<br/>environment](<u>https://test.example.com</u>)</td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_0_Process" -> "Process" [style=dashed, color="#999999", arrowhead=none];
}
