graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Multi-Team System</b></font></td></tr><tr><td align="center"><font point-size="10">@Version(&quot;1.0&quot;)</font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "frontend" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>frontend</b> <i>&lt;task&gt;</i> <i>@Team("Frontend") @Owner("Alice")</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="framework__key" align="left" balign="left">framework</td><td port="framework__value" align="left" balign="left">React</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "backend" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>backend</b> <i>&lt;task&gt;</i> <i>@Team("Backend") @Owner("Bob") @Critical</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="runtime__key" align="left" balign="left">runtime</td><td port="runtime__value" align="left" balign="left">Node.js</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
  "database" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>database</b> <i>&lt;task&gt;</i> <i>@Team("Data") @Owner("Carol") @Critical @ReadOnly</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="engine__key" align="left" balign="left">engine</td><td port="engine__value" align="left" balign="left">PostgreSQL</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];

  // Edges
  "frontend" -> "backend" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "backend" -> "database" [style="dashed", labelOverlay="75%", labelhref="#srcLineTBD"];
}
