digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Order Processing System</b></font></td></tr><tr><td align="center"><font point-size="10">@Version(&quot;2.0&quot;) @Critical</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="environment__key" align="left" balign="left">environment</td><td port="environment__value" align="left" balign="left">production</td></tr><tr><td port="region__key" align="left" balign="left">region</td><td port="region__value" align="left" balign="left">us-east-1</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="paymentProvider__key" align="left" balign="left">paymentProvider : string</td><td port="paymentProvider__value" align="left" balign="left">stripe</td></tr><tr><td port="inventoryDb__key" align="left" balign="left">inventoryDb : URL</td><td port="inventoryDb__value" align="left" balign="left">https://inventory.example.com</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : Duration</td><td port="timeout__value" align="left" balign="left">PT5M</td></tr><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : Integer</td><td port="maxRetries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L7:0-12:2"];
  subgraph cluster_PaymentWorkflow {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>PaymentWorkflow</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "PaymentWorkflow__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "Validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Validate</b> <i>&lt;task&gt;</i> <i>@Critical</i></td></tr><tr><td align="left">Validate Payment Method</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3, URL="#L16:4-16:54"];
    "Process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Process Transaction</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L17:4-17:46"];
    "Confirm" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Confirm</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Confirm Payment</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L18:4-18:35"];
  }
  subgraph cluster_InventoryWorkflow {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>InventoryWorkflow</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "InventoryWorkflow__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "Check" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Check</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check Availability</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L25:4-25:36"];
    "Reserve" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Reserve</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Reserve Items</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L26:4-26:33"];
    "Allocate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Allocate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Allocate Stock</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L27:4-27:35"];
  }
  "OrderReceived" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>OrderReceived</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order received</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L33:0-33:37"];
  "Processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Processing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Processing order</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L34:0-34:36"];
