  "Failed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Failed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order failed</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L36:0-36:28"];

  // Root-level notes
  "note_PaymentWorkflow_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Handles all payment processing with<br/>Stripe integration</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="complexity__key" align="left" balign="left">complexity</td><td port="complexity__value" align="left" balign="left">O(1)</td></tr><tr><td port="owner__key" align="left" balign="left">owner</td><td port="owner__value" align="left" balign="left">Payment Team</td></tr></table></td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_PaymentWorkflow_0" -> "PaymentWorkflow__cluster_anchor" [style=dashed, color="#999999", arrowhead=none];
  "note_InventoryWorkflow_0" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Manages inventory checking and<br/>allocation</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="database__key" align="left" balign="left">database</td><td port="database__value" align="left" balign="left">{{ config.inventoryDb }}</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">{{ config.timeout }}</td></tr></table></td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_InventoryWorkflow_0" -> "InventoryWorkflow__cluster_anchor" [style=dashed, color="#999999", arrowhead=none];

  // Edges
  "OrderReceived" -> "Validate" [labelOverlay="75%", edgeURL="#L39:14-39:41"];
  "Confirm" -> "Check" [labelOverlay="75%", edgeURL="#L40:24-40:50"];
  "Allocate" -> "Processing" [labelOverlay="75%", edgeURL="#L41:27-41:40"];
  "Processing" -> "Completed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">condition</td><td align="left" balign="left">success</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L43:11-43:45", condition="success"];
  "Processing" -> "Failed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">condition</td><td align="left" balign="left">error</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L44:11-44:40", condition="error"];
  "Failed" -> "Validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">retries</td><td align="left" balign="left">3</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L47:7-47:46", retries="3"];
  "Validate" -> "Process" [labelOverlay="75%", edgeURL="#L20:13-20:23"];
  "Process" -> "Confirm" [labelOverlay="75%", edgeURL="#L20:24-20:34"];
  "Check" -> "Reserve" [labelOverlay="75%", edgeURL="#L29:10-29:20"];
  "Reserve" -> "Allocate" [labelOverlay="75%", edgeURL="#L29:21-29:32"];

  // Inferred Dependencies
  "InventoryWorkflow" -> "config" [label="reads config.inventoryDb", style=dashed, color=blue];
  "InventoryWorkflow" -> "config" [label="reads config.timeout", style=dashed, color=blue];
}
