graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Order Processing System</b></font></td></tr><tr><td align="center"><font point-size="10">v @Version(&quot;2.0&quot;) @Critical</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="environment__key" align="left" balign="left">environment</td><td port="environment__value" align="left" balign="left">production</td></tr><tr><td port="region__key" align="left" balign="left">region</td><td port="region__value" align="left" balign="left">us-east-1</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="paymentProvider__key" align="left" balign="left">paymentProvider : string</td><td port="paymentProvider__value" align="left" balign="left">stripe</td></tr><tr><td port="inventoryDb__key" align="left" balign="left">inventoryDb : URL</td><td port="inventoryDb__value" align="left" balign="left">https://inventory.example.com</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : Duration</td><td port="timeout__value" align="left" balign="left">PT5M</td></tr><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : Integer</td><td port="maxRetries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  subgraph cluster_PaymentWorkflow {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>PaymentWorkflow</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "PaymentWorkflow__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "Validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Validate</b> <i>&lt;task&gt;</i> <i>@Critical</i></td></tr><tr><td align="left">Validate Payment Method</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", penwidth=3];
    "Process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Process</b> <i>&lt;task&gt;</i> <i>@Async</i></td></tr><tr><td align="left">Process Transaction</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "Confirm" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Confirm</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Confirm Payment</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  subgraph cluster_InventoryWorkflow {
    label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2"><tr><td align="center"><b>InventoryWorkflow</b> <i>&lt;process&gt;</i></td></tr></table>>;
    labelloc="t";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "InventoryWorkflow__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    "Check" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Check</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check Availability</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "Reserve" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Reserve</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Reserve Items</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
    "Allocate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Allocate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Allocate Stock</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  }
  "OrderReceived" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>OrderReceived</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order received</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Processing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Processing order</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Completed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Completed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order completed</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Failed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Failed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order failed</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "OrderReceived" -> "Validate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Confirm" -> "Check" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Allocate" -> "Processing" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Processing" -> "Completed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">condition=success</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Processing" -> "Failed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">condition=error</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Failed" -> "Validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">retries=3</td></tr></table>>, labelOverlay="75%", labelhref="#srcLineTBD"];
  "Validate" -> "Process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Process" -> "Confirm" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Check" -> "Reserve" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "Reserve" -> "Allocate" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Notes
  "note_0_PaymentWorkflow" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Handles all payment processing with<br/>Stripe integration</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="complexity__key" align="left" balign="left">complexity</td><td port="complexity__value" align="left" balign="left">O(1)</td></tr><tr><td port="owner__key" align="left" balign="left">owner</td><td port="owner__value" align="left" balign="left">Payment Team</td></tr></table></td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_0_PaymentWorkflow" -> "PaymentWorkflow" [style=dashed, color="#999999", arrowhead=none];
  "note_1_InventoryWorkflow" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left">Manages inventory checking and<br/>allocation</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="database__key" align="left" balign="left">database</td><td port="database__value" align="left" balign="left">{{ config.inventoryDb }}</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">{{ config.timeout }}</td></tr></table></td></tr></table>>, shape=note, fillcolor="#FFFACD", style=filled, fontsize=9];
  "note_1_InventoryWorkflow" -> "InventoryWorkflow" [style=dashed, color="#999999", arrowhead=none];

  // Inferred Dependencies
  "InventoryWorkflow" -> "config" [label="reads database", style=dashed, color=blue];
  "InventoryWorkflow" -> "config" [label="reads timeout", style=dashed, color=blue];
}
