graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Simple Workflow</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate Input</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "process" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>process</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process Data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Complete Task</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Start</b></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "End" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>End</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "Start" -> "validate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validate" -> "process" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "process" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "complete" -> "End" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
