graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="value__key" align="left" balign="left">value</td><td port="value__value" align="left" balign="left">shared</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "taskA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>taskA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Use {{ config.value }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "taskB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>taskB</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Also use {{ config.value }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];


  // Inferred Dependencies
  "taskA" -> "config" [label="reads prompt", style=dashed, color=blue];
  "taskB" -> "config" [label="reads prompt", style=dashed, color=blue];
}
