graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "appConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>appConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="apiUrl__key" align="left" balign="left">apiUrl</td><td port="apiUrl__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="apiKey__key" align="left" balign="left">apiKey</td><td port="apiKey__value" align="left" balign="left">#envApiKey</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : Duration</td><td port="timeout__value" align="left" balign="left">PT30S</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "fetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>fetchData</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">{{ appConfig.apiUrl }}</td></tr><tr><td port="auth__key" align="left" balign="left">auth</td><td port="auth__value" align="left" balign="left">Bearer {{ appConfig.apiKey }}</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">{{ appConfig.timeout }}</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];


  // Inferred Dependencies
  "fetchData" -> "appConfig" [label="reads endpoint", style=dashed, color=blue];
  "fetchData" -> "appConfig" [label="reads auth", style=dashed, color=blue];
  "fetchData" -> "appConfig" [label="reads timeout", style=dashed, color=blue];
}
