graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the data and extract key points</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "summarize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>summarize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Summarize based on: {{ analyze.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "report" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>report</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Create report using summary: {{<br/>summarize.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "analyze" -> "summarize" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "summarize" -> "report" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "summarize" -> "analyze" [label="reads prompt", style=dashed, color=blue];
  "report" -> "summarize" [label="reads prompt", style=dashed, color=blue];
}
