graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "defaults" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>defaults</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout : Duration</td><td port="timeout__value" align="left" balign="left">PT30S</td></tr><tr><td port="retries__key" align="left" balign="left">retries : Integer</td><td port="retries__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "production" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>production</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout : Duration</td><td port="timeout__value" align="left" balign="left">PT60S</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "apiCall" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiCall</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">{{ production.timeout }}</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];


  // Inferred Dependencies
  "apiCall" -> "production" [label="reads timeout", style=dashed, color=blue];
}
