graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "userRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userRequest</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="query__key" align="left" balign="left">query</td><td port="query__value" align="left" balign="left">What is machine learning?</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "research" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>research</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Research: {{ userRequest.query }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "synthesize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>synthesize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Synthesize findings from: {{<br/>research.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "format" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>format</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Format final answer based on: {{<br/>synthesize.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "research" -> "synthesize" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "synthesize" -> "format" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "research" -> "userRequest" [label="reads prompt", style=dashed, color=blue];
  "synthesize" -> "research" [label="reads prompt", style=dashed, color=blue];
  "format" -> "synthesize" [label="reads prompt", style=dashed, color=blue];
}
