graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "userRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userRequest</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="query__key" align="left" balign="left">query</td><td port="query__value" align="left" balign="left">What is machine learning?</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L1:0-3:2"];
  "research" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>research</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Research: What is machine learning?</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L5:0-7:2"];
  "synthesize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>synthesize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Synthesize findings from: Research: {{ userRequest.query }}</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L9:0-11:2"];
  "format" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>format</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Format final answer based on: Synthesize findings from: {{ research.prompt }}</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L13:0-15:2"];

  // Edges
  "research" -> "synthesize" [labelOverlay="75%", edgeURL="#L17:9-17:22"];
  "synthesize" -> "format" [labelOverlay="75%", edgeURL="#L17:23-17:32"];

  // Inferred Dependencies
  "research" -> "userRequest" [label="reads userRequest.query", style=dashed, color=blue];
  "synthesize" -> "research" [label="reads research.prompt", style=dashed, color=blue];
  "format" -> "synthesize" [label="reads synthesize.prompt", style=dashed, color=blue];
}
