graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "environment" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>environment</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="name__key" align="left" balign="left">name</td><td port="name__value" align="left" balign="left">production</td></tr><tr><td port="apiUrl__key" align="left" balign="left">apiUrl</td><td port="apiUrl__value" align="left" balign="left">#productionApiUrl</td></tr><tr><td port="debugMode__key" align="left" balign="left">debugMode</td><td port="debugMode__value" align="left" balign="left">false</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "startup" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>startup</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Starting in {{ environment.name }} mode<br/>with API {{ environment.apiUrl }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];


  // Inferred Dependencies
  "startup" -> "environment" [label="reads prompt", style=dashed, color=blue];
  "startup" -> "environment" [label="reads prompt", style=dashed, color=blue];
}
