graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "apiConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="baseUrl__key" align="left" balign="left">baseUrl</td><td port="baseUrl__value" align="left" balign="left">https://api.example.com</td></tr><tr><td port="version__key" align="left" balign="left">version</td><td port="version__value" align="left" balign="left">v1</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "authConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="token__key" align="left" balign="left">token</td><td port="token__value" align="left" balign="left">#envToken</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "makeRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>makeRequest</b> <i>&lt;task&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="url__key" align="left" balign="left">url</td><td port="url__value" align="left" balign="left">{{ apiConfig.baseUrl }}/{{<br align="left"/>apiConfig.version }}/users</td></tr><tr><td port="auth__key" align="left" balign="left">auth</td><td port="auth__value" align="left" balign="left">Bearer {{ authConfig.token }}</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];


  // Inferred Dependencies
  "makeRequest" -> "apiConfig" [label="reads url", style=dashed, color=blue];
  "makeRequest" -> "apiConfig" [label="reads url", style=dashed, color=blue];
  "makeRequest" -> "authConfig" [label="reads auth", style=dashed, color=blue];
}
