graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "userRequest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>userRequest</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="query__key" align="left" balign="left">query</td><td port="query__value" align="left" balign="left">Explain quantum computing</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "retrieve" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>retrieve</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Find information about: {{<br/>userRequest.query }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "analyze" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Analyze the following information: {{<br/>retrieve.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "summarize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>summarize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Summarize this analysis: {{<br/>analyze.prompt }}</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "retrieve" -> "analyze" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze" -> "summarize" [labelOverlay="75%", labelhref="#srcLineTBD"];

  // Inferred Dependencies
  "retrieve" -> "userRequest" [label="reads prompt", style=dashed, color=blue];
  "analyze" -> "retrieve" [label="reads prompt", style=dashed, color=blue];
  "summarize" -> "analyze" [label="reads prompt", style=dashed, color=blue];
}
