graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "apiConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apiConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="baseUrl__key" align="left" balign="left">baseUrl</td><td port="baseUrl__value" align="left" balign="left">https://api.example.com</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L1:0-3:2"];
  "dbConfig" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>dbConfig</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="host__key" align="left" balign="left">host</td><td port="host__value" align="left" balign="left">localhost</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L5:0-7:2"];
  "setup" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>setup</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left"><font point-size="8" color="#666666">â†’ API: https://api.example.com, DB: localhost</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L9:0-11:2"];


  // Inferred Dependencies
  "setup" -> "apiConfig" [label="reads apiConfig.baseUrl", style=dashed, color=blue];
  "setup" -> "dbConfig" [label="reads dbConfig.host", style=dashed, color=blue];
}
