graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="apiKey__key" align="left" balign="left">apiKey</td><td port="apiKey__value" align="left" balign="left">secret</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout</td><td port="timeout__value" align="left" balign="left">30</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "authenticate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>authenticate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Authenticate with key {{ config.apiKey<br/>}} and timeout {{ config.timeout }}</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];


  // Inferred Dependencies
  "authenticate" -> "config" [label="reads prompt", style=dashed, color=blue];
  "authenticate" -> "config" [label="reads prompt", style=dashed, color=blue];
}
