graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "taskA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>taskA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Generate a summary</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L1:0-3:2"];
  "taskB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>taskB</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Use result from {{ taskA.prompt }}</td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L5:0-7:2"];


  // Inferred Dependencies
  "taskB" -> "taskA" [label="reads taskA.prompt", style=dashed, color=blue];
}
