graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Bidirectional Machine</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr><tr><td port="maxSteps__key" align="left" balign="left">maxSteps</td><td port="maxSteps__value" align="left" balign="left">20</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "NavigationState" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>NavigationState</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="forwardCount__key" align="left" balign="left">forwardCount</td><td port="forwardCount__value" align="left" balign="left">0</td></tr><tr><td port="backwardCount__key" align="left" balign="left">backwardCount</td><td port="backwardCount__value" align="left" balign="left">0</td></tr><tr><td port="maxBounces__key" align="left" balign="left">maxBounces</td><td port="maxBounces__value" align="left" balign="left">3</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L6:0-10:1"];
  "nodeA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>nodeA</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Node A</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Decide whether to move forward or backward</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L12:0-14:1"];
  "nodeB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>nodeB</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Node B</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Decide whether to continue or return</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L16:0-18:1"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr><tr><td align="left">Navigation complete</td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000", URL="#L20:0-20:25"];

  // Edges
  "nodeA" -> "nodeB" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>move to B</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L22:6-22:26"];
  "nodeB" -> "nodeA" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>return to A</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L23:6-23:28"];
  "nodeB" -> "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>complete</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L24:6-24:23"];
}
