  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "TaskContext" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>TaskContext</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="dataQuality__key" align="left" balign="left">dataQuality</td><td port="dataQuality__value" align="left" balign="left">unknown</td></tr><tr><td port="processingMode__key" align="left" balign="left">processingMode</td><td port="processingMode__value" align="left" balign="left">standard</td></tr><tr><td port="errorCount__key" align="left" balign="left">errorCount</td><td port="errorCount__value" align="left" balign="left">0</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L6:0-10:1"];
  "qualityCheck" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>qualityCheck</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Quality Assessment</td></tr><tr><td align="left"><font point-size="9"><i>Assess data quality</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Evaluate data quality and set processing mode</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Data Quality Check</td></tr><tr><td port="checks__key" align="left" balign="left">checks</td><td port="checks__value" align="left" balign="left">[<BR/>  &quot;completeness&quot;,<BR/>  &quot;accuracy&quot;,<BR/>  &quot;consistency&quot;<BR/>]</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L12:0-17:1"];
  "standardProcessing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>standardProcessing</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Standard Processing</td></tr><tr><td align="left"><font point-size="9"><i>Standard data processing</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Process data using standard algorithms</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Standard Processing</td></tr><tr><td port="mode__key" align="left" balign="left">mode</td><td port="mode__value" align="left" balign="left">standard</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L19:0-24:1"];
  "enhancedProcessing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>enhancedProcessing</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Enhanced Processing</td></tr><tr><td align="left"><font point-size="9"><i>Enhanced data processing with quality improvements</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Process data with enhanced quality controls</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Enhanced Processing</td></tr><tr><td port="mode__key" align="left" balign="left">mode</td><td port="mode__value" align="left" balign="left">enhanced</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L26:0-31:1"];
  "errorHandling" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>errorHandling</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Error Handling</td></tr><tr><td align="left"><font point-size="9"><i>Handle processing errors</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Handle and recover from processing errors</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Error Handling</td></tr><tr><td port="mode__key" align="left" balign="left">mode</td><td port="mode__value" align="left" balign="left">recovery</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L33:0-38:1"];
