  // Edges
  "qualityCheck" -> "standardProcessing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">TaskContext.dataQuality == 'good'</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L42:13-42:76", when="TaskContext.dataQuality == 'good", style=solid, color="#4CAF50", penwidth=2];
  "qualityCheck" -> "enhancedProcessing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">TaskContext.dataQuality == 'poor'</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L43:13-43:76", when="TaskContext.dataQuality == 'poor", style=solid, color="#4CAF50", penwidth=2];
  "qualityCheck" -> "errorHandling" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">TaskContext.errorCount &gt; 0</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L44:13-44:64", when="TaskContext.errorCount > 0", style=solid, color="#4CAF50", penwidth=2];
  "standardProcessing" -> "end" [labelOverlay="75%", edgeURL="#L46:19-46:25"];
  "enhancedProcessing" -> "end" [labelOverlay="75%", edgeURL="#L47:19-47:25"];
  "errorHandling" -> "end" [labelOverlay="75%", edgeURL="#L48:14-48:20"];

  // Inferred Dependencies
  "qualityCheck" -> "TaskContext" [label="condition references TaskContext", style=dashed, color=blue];
  "qualityCheck" -> "TaskContext" [label="condition references TaskContext", style=dashed, color=blue];
}
