digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Context Mutation Machine</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr><tr><td port="maxSteps__key" align="left" balign="left">maxSteps</td><td port="maxSteps__value" align="left" balign="left">15</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "MutationTest" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>MutationTest</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="counter__key" align="left" balign="left">counter</td><td port="counter__value" align="left" balign="left">0</td></tr><tr><td port="data__key" align="left" balign="left">data</td><td port="data__value" align="left" balign="left">[]</td></tr><tr><td port="flags__key" align="left" balign="left">flags</td><td port="flags__value" align="left" balign="left">{<BR/>  &quot;modified&quot;: false,<BR/>  &quot;validated&quot;: false<BR/>}</td></tr></table></td></tr></table>>, pad=0.5, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", URL="#L6:0-13:1"];
  "mutator1" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>mutator1</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">First Mutator</td></tr><tr><td align="left"><font point-size="9"><i>Mutate context data</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Modify context counter and data array</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">First Mutator</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L15:0-19:1"];
  "mutator2" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>mutator2</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Second Mutator</td></tr><tr><td align="left"><font point-size="9"><i>Further mutate context</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Further modify context and set flags</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Second Mutator</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L21:0-25:1"];
  "validator" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validator</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Context Validator</td></tr><tr><td align="left"><font point-size="9"><i>Validate context state</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Validate that all context mutations persisted correctly</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Context Validator</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L27:0-31:1"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr><tr><td align="left">Context validated</td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000", URL="#L33:0-33:23"];

  // Edges
  "mutator1" -> "mutator2" [labelOverlay="75%", edgeURL="#L35:9-35:20"];
  "mutator2" -> "validator" [labelOverlay="75%", edgeURL="#L35:21-35:33"];
  "validator" -> "end" [labelOverlay="75%", edgeURL="#L35:34-35:40"];
}
