digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Error Recovery Machine</b></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="logLevel__key" align="left" balign="left">logLevel</td><td port="logLevel__value" align="left" balign="left">debug</td></tr><tr><td port="maxSteps__key" align="left" balign="left">maxSteps</td><td port="maxSteps__value" align="left" balign="left">15</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b></td></tr><tr><td align="left">Risky operation</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Attempt operation that might fail</font></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L6:0-8:1"];
  "retry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>retry</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Retry operation</td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Retry the failed operation</font></td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L10:0-12:1"];
  "success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>success</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Operation succeeded</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L14:0-14:35"];
  "failure" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>failure</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Operation failed permanently</td></tr></table>>, pad=0.5, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2", URL="#L15:0-15:44"];
  "end" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>end</b></td></tr><tr><td align="left">Process complete</td></tr></table>>, pad=0.5, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000", URL="#L16:0-16:22"];

  // Edges
  "start" -> "success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>success</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L18:6-18:26"];
  "start" -> "retry" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>retry</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L19:6-19:22"];
  "start" -> "failure" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>failure</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L20:6-20:26"];
  "retry" -> "success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>retry success</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L21:6-21:32"];
  "retry" -> "failure" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left"><b>retry failure</b></td></tr></table>>, labelOverlay="75%", edgeURL="#L22:6-22:32"];
  "success" -> "end" [labelOverlay="75%", edgeURL="#L23:8-23:14"];
  "failure" -> "end" [labelOverlay="75%", edgeURL="#L24:8-24:14"];
}
