  "errorRecovery" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>errorRecovery</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Error Recovery</td></tr><tr><td align="left"><font point-size="9"><i>Recover from persistent errors</i></font></td></tr><tr><td align="left"><font point-size="8" color="#666666">→ Implement recovery strategy for persistent failures</font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="title__key" align="left" balign="left">title</td><td port="title__value" align="left" balign="left">Error Recovery</td></tr><tr><td port="fallbackMode__key" align="left" balign="left">fallbackMode</td><td port="fallbackMode__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, pad=0.5, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2", URL="#L28:0-33:1"];
