  "riskyOperation" -> "errorRecovery" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="2" align="left"><tr><td align="left"><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td align="left" balign="left">when</td><td align="left" balign="left">ErrorContext.attemptCount &gt;= ErrorContext.maxRetries</td></tr></table></td></tr></table>>, labelOverlay="75%", edgeURL="#L45:15-45:92", when="ErrorContext.attemptCount >= ErrorContext.maxRetries", style=solid, color="#4CAF50", penwidth=2];
