graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Refined styling, ports, and layout</b></font></td></tr><tr><td align="center"><font point-size="10"><i>my machine description</i></font></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="type__key" align="left" balign="left">type</td><td port="type__value" align="left" balign="left">example</td></tr></table></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  subgraph cluster_parent {
    label="";
    style=filled;
    fontsize=10;
    fillcolor="#FFFFFF";
    color="#999999";
    "parent" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>parent</b></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="spouse__key" align="left" balign="left">spouse</td><td port="spouse__value" align="left" balign="left">Alice</td></tr></table></td></tr></table>>, shape=plain, margin=0];
    "parent__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

    subgraph cluster_child1 {
      label="";
      style=filled;
      fontsize=10;
      fillcolor="#FFFFFF";
      color="#999999";
      "child1" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left" port="cluster_header"><b>child1</b> <i>@highlight</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="age__key" align="left" balign="left">age</td><td port="age__value" align="left" balign="left">38</td></tr></table></td></tr></table>>, shape=plain, margin=0];
      "child1__cluster_anchor" [shape=point, width=0.01, height=0.01, label="", style=invis, fixedsize=true];

      "grandchild" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>grandchild</b></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="age__key" align="left" balign="left">age</td><td port="age__value" align="left" balign="left">7</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
    }
    "child2" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>child2</b> <i>@highlight</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="likes__key" align="left" balign="left">likes</td><td port="likes__value" align="left" balign="left">apples</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C", color="red", rank="group:one", shape="star"];
  }
  "apples" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>apples</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "parent":"spouse__value" -> "child1__cluster_anchor" [label="begets...", labelOverlay="75%", labelhref="#srcLineTBD", lhead="cluster_child1"];
  "child1__cluster_anchor" -> "child2" [label="@highlight @style(\"color: yellow;\\ngradientangle: 90\") @highlight\\n@style(\"color: yellow; gradientangle:\\n90\")", labelOverlay="75%", labelhref="#srcLineTBD", ltail="cluster_child1", color="red", rank="group:one", shape="star"];
  "child2":"likes__value" -> "apples" [label="likes", labelOverlay="75%", labelhref="#srcLineTBD"];
}
