graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Phase 4: Graph Validation Examples</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;init&gt;</i></td></tr><tr><td align="left">Entry point for execution</td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "fetchData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>fetchData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Fetch data from API</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "processData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Process the fetched data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "saveData" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>saveData</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Save processed data</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "Success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Success</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Successful completion</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "Error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>Error</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Error occurred</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "retryLoop" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>retryLoop</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Task with retry mechanism</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "checkStatus" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>checkStatus</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Check if retry is needed</td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "config" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>config</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="maxRetries__key" align="left" balign="left">maxRetries : number</td><td port="maxRetries__value" align="left" balign="left">3</td></tr><tr><td port="timeout__key" align="left" balign="left">timeout : number</td><td port="timeout__value" align="left" balign="left">5000</td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];

  // Edges
  "start" -> "fetchData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "fetchData" -> "processData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "processData" -> "saveData" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "saveData" -> "Success" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "saveData" -> "Error" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "retryLoop" -> "checkStatus" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "checkStatus" -> "retryLoop" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
