graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Auto Transitions Demo</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "idle" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>idle</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "processing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>processing</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "error" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>error</b></td></tr></table>>, shape=box, fillcolor="#FFFFFF", style=filled, color="#000000"];

  // Edges
  "idle" -> "processing" [label="@auto @auto", labelOverlay="75%", labelhref="#srcLineTBD"];
  "processing" -> "error" [label="when=errorCount > 3", labelOverlay="75%", labelhref="#srcLineTBD"];
}
