graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>CI/CD Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "source" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>source</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Checkout source code</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">init</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "build" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>build</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Compile and build</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">build</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "test" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>test</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Run test suite</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">test</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "security_scan" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>security_scan</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Security vulnerability scan</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">security</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "deploy_staging" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>deploy_staging</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deploy to staging</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">deploy</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "integration_test" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>integration_test</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Run integration tests</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">test</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "deploy_production" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>deploy_production</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Deploy to production</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="stage__key" align="left" balign="left">stage</td><td port="stage__value" align="left" balign="left">deploy</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "failed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>failed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Pipeline failed</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "success" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>success</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Pipeline completed successfully</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "source" -> "build" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "build" -> "test" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "test" -> "security_scan" [label="pass", labelOverlay="75%", labelhref="#srcLineTBD"];
  "security_scan" -> "deploy_staging" [label="clean", labelOverlay="75%", labelhref="#srcLineTBD"];
  "deploy_staging" -> "integration_test" [label="ready", labelOverlay="75%", labelhref="#srcLineTBD"];
  "integration_test" -> "deploy_production" [label="pass", labelOverlay="75%", labelhref="#srcLineTBD"];
  "deploy_production" -> "success" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "build" -> "failed" [label="error", labelOverlay="75%", labelhref="#srcLineTBD"];
  "test" -> "failed" [label="failure", labelOverlay="75%", labelhref="#srcLineTBD"];
  "security_scan" -> "failed" [label="vulnerability", labelOverlay="75%", labelhref="#srcLineTBD"];
  "integration_test" -> "failed" [label="failure", labelOverlay="75%", labelhref="#srcLineTBD"];
}
