graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Data Pipeline</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "fetch" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>fetch</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Fetch data</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="source__key" align="left" balign="left">source</td><td port="source__value" align="left" balign="left">api</td></tr><tr><td port="endpoint__key" align="left" balign="left">endpoint</td><td port="endpoint__value" align="left" balign="left">https://api.example.com/data</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "transform" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>transform</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Transform data</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="operations__key" align="left" balign="left">operations</td><td port="operations__value" align="left" balign="left">[<br align="left"/>  &quot;filter&quot;,<br align="left"/>  &quot;map&quot;,<br align="left"/>  &quot;reduce&quot;<br align="left"/>]</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "validate" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>validate</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Validate results</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="schema__key" align="left" balign="left">schema</td><td port="schema__value" align="left" balign="left">#validationSchema</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "store" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>store</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Store results</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="destination__key" align="left" balign="left">destination</td><td port="destination__value" align="left" balign="left">database</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];

  // Edges
  "fetch" -> "transform" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "transform" -> "validate" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "validate" -> "store" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
