graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>E-Commerce Order Processing</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;init&gt;</i></td></tr></table>>, shape=ellipse, fillcolor="#FFF3E0", style=filled, color="#F57C00"];
  "new_order" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>new_order</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order received</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="priority__key" align="left" balign="left">priority : number</td><td port="priority__value" align="left" balign="left">1</td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "payment_pending" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>payment_pending</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Awaiting payment</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "payment_confirmed" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>payment_confirmed</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Payment successful</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "preparing" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>preparing</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order being prepared</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "shipped" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>shipped</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order shipped to customer</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "delivered" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>delivered</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order delivered</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "cancelled" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>cancelled</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order cancelled</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "refunded" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>refunded</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Order refunded</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "start" -> "new_order" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "new_order" -> "payment_pending" [label="create", labelOverlay="75%", labelhref="#srcLineTBD"];
  "payment_pending" -> "payment_confirmed" [label="pay", labelOverlay="75%", labelhref="#srcLineTBD"];
  "payment_confirmed" -> "preparing" [label="prepare", labelOverlay="75%", labelhref="#srcLineTBD"];
  "preparing" -> "shipped" [label="ship", labelOverlay="75%", labelhref="#srcLineTBD"];
  "shipped" -> "delivered" [label="deliver", labelOverlay="75%", labelhref="#srcLineTBD"];
  "payment_pending" -> "cancelled" [label="timeout=900", labelOverlay="75%", labelhref="#srcLineTBD"];
  "payment_confirmed" -> "refunded" [label="cancel_request", labelOverlay="75%", labelhref="#srcLineTBD"];
  "preparing" -> "refunded" [label="cancel_request", labelOverlay="75%", labelhref="#srcLineTBD"];
}
