graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>Smart Task Prioritization System</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "start" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>start</b> <i>&lt;state&gt;</i></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "analyze_tasks" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analyze_tasks</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">You are helping prioritize a list of<br/>tasks. Here are the tasks: 1) Fix<br/>critical security bug, 2) Write<br/>documentation, 3) Implement new feature,<br/>4) Code review. Analyze these tasks and<br/>use set_context_value to store a<br/>prioritized list in the 'analysis'<br/>context node with key 'prioritizedTasks'<br/>as a JSON string containing an array of<br/>objects with fields: task, priority<br/>(1-4), reasoning.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "analysis" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>analysis</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="prioritizedTasks__key" align="left" balign="left">prioritizedTasks : string</td><td port="prioritizedTasks__value" align="left" balign="left">[]</td></tr><tr><td port="analysisDate__key" align="left" balign="left">analysisDate : string</td><td port="analysisDate__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "generate_action_plan" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>generate_action_plan</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Read the prioritized tasks from<br/>'analysis' context using<br/>get_context_value. Create a detailed<br/>action plan for the top 2 priority<br/>tasks. Use set_context_value to store<br/>the action plan in 'action_plan' context<br/>with key 'plan' as a string with clear<br/>steps for each task.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "action_plan" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>action_plan</b> <i>&lt;context&gt;</i></td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="plan__key" align="left" balign="left">plan : string</td><td port="plan__value" align="left" balign="left"></td></tr><tr><td port="estimatedTime__key" align="left" balign="left">estimatedTime : string</td><td port="estimatedTime__value" align="left" balign="left"></td></tr></table></td></tr></table>>, shape=folder, fillcolor="#E8F5E9", style=filled, color="#388E3C"];
  "finalize" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>finalize</b> <i>&lt;task&gt;</i></td></tr><tr><td align="left">Read the action plan from 'action_plan'<br/>context. Summarize the workflow<br/>completion and use the transition tool<br/>to move to 'complete' state.</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="meta__key" align="left" balign="left">meta</td><td port="meta__value" align="left" balign="left">true</td></tr></table></td></tr></table>>, shape=box, fillcolor="#E3F2FD", style=filled, color="#1976D2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Task prioritization workflow completed</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "start" -> "analyze_tasks" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "analyze_tasks" -> "analysis" [label="analyzed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "analysis" -> "generate_action_plan" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "generate_action_plan" -> "action_plan" [label="planned", labelOverlay="75%", labelhref="#srcLineTBD"];
  "action_plan" -> "finalize" [labelOverlay="75%", labelhref="#srcLineTBD"];
  "finalize" -> "complete" [labelOverlay="75%", labelhref="#srcLineTBD"];
}
