graphviz:
digraph {
  // Graph attributes
  label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="center"><font point-size="12"><b>User Onboarding Workflow</b></font></td></tr></table>>;
  labelloc="t";
  fontsize=10;
  fontname="Arial";
  compound=true;
  rankdir=TB;
  pad=0.25;
  node [fontname="Arial", fontsize=10];
  edge [fontname="Arial", fontsize=9];

  // Node definitions with nested namespaces
  "registration" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>registration</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">User fills registration form</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="status__key" align="left" balign="left">status</td><td port="status__value" align="left" balign="left">active</td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "email_verification" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>email_verification</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Email verification pending</td></tr><tr><td><table border="0" cellborder="1" cellspacing="0" cellpadding="2" align="left"><tr><td port="timeout__key" align="left" balign="left">timeout : Integer</td><td port="timeout__value" align="left" balign="left">3600</td></tr></table></td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "profile_setup" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>profile_setup</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">User customizes profile</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "complete" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>complete</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">Onboarding complete</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];
  "abandoned" [label=<<table border="0" cellborder="0" cellspacing="0" cellpadding="4"><tr><td align="left"><b>abandoned</b> <i>&lt;state&gt;</i></td></tr><tr><td align="left">User abandoned the process</td></tr></table>>, shape=diamond, fillcolor="#F3E5F5", style=filled, color="#7B1FA2"];

  // Edges
  "registration" -> "email_verification" [label="submit", labelOverlay="75%", labelhref="#srcLineTBD"];
  "email_verification" -> "profile_setup" [label="verified", labelOverlay="75%", labelhref="#srcLineTBD"];
  "profile_setup" -> "complete" [label="completed", labelOverlay="75%", labelhref="#srcLineTBD"];
  "email_verification" -> "abandoned" [label="timeout", labelOverlay="75%", labelhref="#srcLineTBD"];
  "registration" -> "abandoned" [label="cancel", labelOverlay="75%", labelhref="#srcLineTBD"];
}
